// Ultra-robust Parser for SHRAVYA Voice Synthesis
try {
  const inputText = $json.clean_text || $json.output || $json.text || 'I am here to help you.';

  if (!inputText || typeof inputText !== 'string' || inputText.trim() === '') {
    return [{
      json: {
        clean_text: 'Hello, I am SHRAVYA, ready to assist you.',
        ready_for_tts: true,
        output: 'Hello, I am SHRAVYA, ready to assist you.'
      }
    }];
  }

  // Enhanced cleaning with safety checks
  let cleanedText = String(inputText)
    // Remove Unicode emojis safely
    .replace(/[\u{1F600}-\u{1F64F}]/gu, '')
    .replace(/[\u{1F300}-\u{1F5FF}]/gu, '')
    .replace(/[\u{1F680}-\u{1F6FF}]/gu, '')
    .replace(/[\u{2600}-\u{26FF}]/gu, '')
    .replace(/[\u{2700}-\u{27BF}]/gu, '')

    // Remove markdown and special characters safely
    .replace(/\*\*([^*]*)\*\*/g, '$1')
    .replace(/\*([^*]*)\*/g, '$1')
    .replace(/__([^_]*)__/g, '$1')
    .replace(/~~([^~]*)~~/g, '$1')
    .replace(/`([^`]*)`/g, '$1')

    // Clean problematic characters for JSON
    .replace(/"/g, "'")  // Replace quotes
    .replace(/\\/g, "")  // Remove backslashes
    .replace(/\n/g, ' ') // Line breaks to spaces
    .replace(/\t/g, ' ') // Tabs to spaces
    .replace(/\s+/g, ' ') // Multiple spaces to single
    .trim();

  // Ensure minimum viable message
  if (cleanedText.length < 5) {
    cleanedText = 'I understand and I am here to help.';
  }

  // Ensure maximum length for TTS
  if (cleanedText.length > 500) {
    cleanedText = cleanedText.substring(0, 497) + '...';
  }

  return [{
    json: {
      clean_text: cleanedText,
      ready_for_tts: true,
      output: cleanedText,
      character_count: cleanedText.length
    }
  }];

} catch (error) {
  return [{
    json: {
      clean_text: 'I apologize, there was a processing issue. I am here to assist you.',
      ready_for_tts: true,
      output: 'I apologize, there was a processing issue. I am here to assist you.',
      error_handled: true
    }
  }];
}

// EEG Real-time Processor - Complete Robust Version
try {
  const inputData = $input.all()[0].json;

  // Debug: Log what we received
  console.log('Input data received:', JSON.stringify(inputData, null, 2));

  // CRITICAL: Extract cognitive states from the correct path in input data
  // Check multiple possible locations where cognitive_states might be
  let cognitiveStates = {};

  if (inputData.cognitive_states && typeof inputData.cognitive_states === 'object') {
    cognitiveStates = inputData.cognitive_states;
  } else if (inputData.body && inputData.body.cognitive_states) {
    cognitiveStates = inputData.body.cognitive_states;
  } else if (inputData.json && inputData.json.cognitive_states) {
    cognitiveStates = inputData.json.cognitive_states;
  } else {
    console.log('No cognitive_states found in input, using defaults');
    cognitiveStates = {
      focus: 5, stress: 5, anxiety: 5, fatigue: 5, calm: 5, flow_state: 5
    };
  }

  console.log('Extracted cognitive states:', JSON.stringify(cognitiveStates, null, 2));

  // Ensure all cognitive states are properly preserved as numbers
  const preservedCognitiveStates = {
    focus: typeof cognitiveStates.focus === 'number' ? cognitiveStates.focus : parseFloat(cognitiveStates.focus) || 0,
    stress: typeof cognitiveStates.stress === 'number' ? cognitiveStates.stress : parseFloat(cognitiveStates.stress) || 0,
    anxiety: typeof cognitiveStates.anxiety === 'number' ? cognitiveStates.anxiety : parseFloat(cognitiveStates.anxiety) || 0,
    fatigue: typeof cognitiveStates.fatigue === 'number' ? cognitiveStates.fatigue : parseFloat(cognitiveStates.fatigue) || 0,
    calm: typeof cognitiveStates.calm === 'number' ? cognitiveStates.calm : parseFloat(cognitiveStates.calm) || 0,
    flow_state: typeof cognitiveStates.flow_state === 'number' ? cognitiveStates.flow_state : parseFloat(cognitiveStates.flow_state) || 0
  };

  console.log('Preserved cognitive states:', JSON.stringify(preservedCognitiveStates, null, 2));

  // Calculate wellness score from the PRESERVED data
  const wellnessScore = (
    (preservedCognitiveStates.focus * 0.3) +
    (preservedCognitiveStates.calm * 0.2) +
    (preservedCognitiveStates.flow_state * 0.2) +
    ((10 - preservedCognitiveStates.stress) * 0.15) +
    ((10 - preservedCognitiveStates.anxiety) * 0.1) +
    ((10 - preservedCognitiveStates.fatigue) * 0.05)
  );

  // Create the output data structure
  const outputData = {
    // Preserve ALL original input fields
    user_id: inputData.user_id || inputData.body?.user_id || 'test_user',
    session_id: inputData.session_id || inputData.body?.session_id || `eeg_${Date.now()}`,
    device_id: inputData.device_id || inputData.body?.device_id || 'SHRAVYA_DEVICE',
    timestamp: inputData.timestamp || inputData.body?.timestamp || new Date().toISOString(),

    // Preserve frequency analysis if it exists
    frequency_analysis: inputData.frequency_analysis || inputData.body?.frequency_analysis || {
      delta_power: 0.15, theta_power: 0.23, alpha_power: 0.42, beta_power: 0.28, gamma_power: 0.12
    },

    // Preserve signal quality if it exists
    signal_quality: inputData.signal_quality || inputData.body?.signal_quality || {
      snr_db: 18.5, artifact_detected: false
    },

    // Preserve sampling rate
    sampling_rate: inputData.sampling_rate || inputData.body?.sampling_rate || 500,

    // MOST IMPORTANT: Preserve the cognitive states with original values
    cognitive_states: preservedCognitiveStates,

    // Add wellness assessment based on preserved data
    wellness_assessment: {
      overall_score: parseFloat(wellnessScore.toFixed(1)),
      intervention_level: "pending", // Let Proactive Trigger decide
      insights: [] // Let Proactive Trigger populate
