    // Add session context
    session_context: {
      data_quality: (inputData.signal_quality?.snr_db || inputData.body?.signal_quality?.snr_db) ? 
        Math.min((inputData.signal_quality?.snr_db || inputData.body?.signal_quality?.snr_db) / 20, 1.0) : 0.85,
