// Helper function for default responses
function generateDefaultResponse(wellness, stress, anxiety, focus) {
  if (wellness >= 7) {
    return `Excellent wellness detected! Your wellness score is ${wellness}/10. You're maintaining great cognitive balance.`;
  } else if (wellness >= 5) {
    return `Good cognitive state with wellness score ${wellness}/10. ${stress >= 5 ? 'Consider some relaxation techniques.' : 'Keep up the balanced approach.'}`;
  } else if (wellness >= 3) {
    return `Moderate wellness level at ${wellness}/10. ${stress >= 6 ? 'Elevated stress detected - try breathing exercises.' : 'Focus on self-care activities.'}`;
  } else {
    return `URGENT: Critical wellness crisis detected with extremely high stress and anxiety levels | TOOLS: gmail,spotify | IMMEDIATE INTERVENTION REQUIRED.`;
  }
}

try {
  const currentData = $input.all()[0].json || {};
  console.log('EEG Memory Co-ordinator INPUT:', JSON.stringify(currentData, null, 2));

  const eegAnalysis = currentData.output || 'EEG analysis pending';

  // Handle session timing more robustly
  const now = new Date();
  let sessionStart = currentData.session_start || currentData.timestamp;
  if (sessionStart) {
    sessionStart = new Date(sessionStart);
  } else {
    sessionStart = new Date(now.getTime() - (60 * 60 * 1000)); // Default: 1 hour ago
  }

  const sessionDuration = Math.round((now - sessionStart) / (1000 * 60));
// SESSION CONTINUITY LOGIC - ADD THIS
  const maxSessionDuration = 120; // 2 hours in minutes
  const stressStillHigh = (currentData.cognitive_states?.stress || 0) >= 6;
  const anxietyStillHigh = (currentData.cognitive_states?.anxiety || 0) >= 6;
  const interventionJustApplied = currentData.action_plan?.emergency_contact || currentData.action_plan?.calming_music;
  const sessionOngoing = sessionDuration < maxSessionDuration && (stressStillHigh || anxietyStillHigh);

  // Only calculate intervention effectiveness if we have the required data
  const interventionEffectiveness = {};
  if (currentData.interventions_applied && 
      Array.isArray(currentData.interventions_applied) &&
      currentData.pre_intervention_states && 
      currentData.cognitive_states) {

    currentData.interventions_applied.forEach(intervention => {
      const preStress = currentData.pre_intervention_states.stress || 1;
      const preAnxiety = currentData.pre_intervention_states.anxiety || 1;
      const postStress = currentData.cognitive_states.stress || 0;
      const postAnxiety = currentData.cognitive_states.anxiety || 0;

      const stressReduction = Math.max(0, (preStress - postStress) / preStress);
      const anxietyReduction = Math.max(0, (preAnxiety - postAnxiety) / preAnxiety);

      interventionEffectiveness[intervention] = Math.round(Math.max(stressReduction, anxietyReduction) * 100) / 100;
    });
  }

  // Create comprehensive session data with proper fallbacks
  const sessionData = {
    user_id: currentData.user_id || 'default_user',
    session_id: currentData.session_id || `eeg_session_${Date.now()}`,
    device_id: currentData.device_id || 'SHRAVYA_DEVICE',
    session_start: sessionStart.toISOString(),
    session_end: now.toISOString(),
    session_duration_minutes: sessionDuration,

    // Use actual cognitive states from input - these are the key fields!
    cognitive_states: {
      focus: currentData.cognitive_states?.focus ?? null,
      stress: currentData.cognitive_states?.stress ?? null,
      anxiety: currentData.cognitive_states?.anxiety ?? null,
      calm: currentData.cognitive_states?.calm ?? null,
      fatigue: currentData.cognitive_states?.fatigue ?? null,
      flow_state: currentData.cognitive_states?.flow_state ?? null
