    // Calculate wellness score from cognitive states if not provided
    wellness_score: currentData.wellness_assessment?.overall_score || 
                   currentData.wellness_score || 
                   calculateWellnessScore(currentData.cognitive_states),

    interventions_applied: currentData.interventions_applied || [],
    intervention_effectiveness: interventionEffectiveness,

    session_insights: eegAnalysis,

    // Additional metrics
    frequency_analysis: currentData.frequency_analysis || null,
    signal_quality: currentData.signal_quality || null,
    sampling_rate: currentData.sampling_rate || null,
    data_quality: currentData.session_context?.data_quality || 0.85,
    device_health: currentData.session_context?.device_health !== false,
    stored_at: now.toISOString()
  };

  console.log('EEG Memory Co-ordinator OUTPUT:', JSON.stringify(sessionData, null, 2));

  return [{
    json: {
      ...sessionData,  // Include all session data at root level for downstream nodes
      mongodb_data: sessionData,
      session_complete: true,
      ready_for_storage: true,
      session_ongoing: sessionOngoing,
      continue_monitoring: sessionOngoing,
