// Helper function to calculate wellness score
function calculateWellnessScore(cognitiveStates) {
  if (!cognitiveStates) return null;

  const focus = cognitiveStates.focus || 0;
  const stress = cognitiveStates.stress || 0;
  const anxiety = cognitiveStates.anxiety || 0;
  const calm = cognitiveStates.calm || 0;
  const fatigue = cognitiveStates.fatigue || 0;
  const flow = cognitiveStates.flow_state || 0;

  const score = (
    (focus * 0.3) +
    (calm * 0.2) +
    (flow * 0.2) +
    ((10 - stress) * 0.15) +
    ((10 - anxiety) * 0.1) +
    ((10 - fatigue) * 0.05)
  );

  return Math.round(score * 10) / 10;
}

