// Helper functions for dynamic calculations
function calculateTrend(values) {
  if (values.length < 2) return 0;
  const recent = values.slice(-3).reduce((a, b) => a + b, 0) / 3;
  const older = values.slice(0, -3).reduce((a, b) => a + b, 0) / (values.length - 3);
  return (recent - older) / older;
}

function generateKeyFindings(cognitiveStates) {
  const findings = [];
  if (cognitiveStates.focus > 7) findings.push("High focus achieved");
  if (cognitiveStates.stress < 3) findings.push("Low stress levels maintained");
  if (cognitiveStates.anxiety < 3) findings.push("Calm and relaxed state");
  return findings.join(", ") || "Balanced cognitive state observed";
}

function generateRecommendations(sessions) {
  const recommendations = [];
  const avgStress = sessions.reduce((sum, s) => sum + s.cognitive_states.stress, 0) / sessions.length;
  const avgFocus = sessions.reduce((sum, s) => sum + s.cognitive_states.focus, 0) / sessions.length;

  if (avgStress > 5) recommendations.push("Consider incorporating more stress-reduction techniques");
  if (avgFocus < 6) recommendations.push("Try scheduling focused work during your peak attention hours");

  return recommendations.length > 0 ? recommendations : ["Continue your current wellness practices - you're doing great!"];
}

